import {Colors} from "./Colors";
import {Figure, FigureNames} from "./figures/Figure";
import {Board} from "./Board";

export class Cell{
    readonly _x: number;
    readonly _y: number;
    readonly _color: Colors;
    _figure: Figure | null;
    _board: Board;
    _available: boolean;
    _id: number;

    constructor(board: Board, x: number, y: number, color: Colors, figure: Figure | null) {
        this._board = board;
        this._x = x;
        this._y = y;
        this._color = color;
        this._figure = figure;
        this._available = false;
        this._id = Math.random()
    }

    isEmpty(): boolean {
        return this._figure === null;
    }

    isEnemy(target: Cell): boolean {
        if (target._figure){
            return this._figure?._color !== target._figure._color;
        }
        return false;
    }

    isEmptyVertical(target: Cell): boolean {
        if (this._x !== target._x)
            return false;

        const min = Math.min(this._y, target._y);
        const max = Math.max(this._y, target._y);

        for (let y = min + 1; y < max; y++){
            if (!this._board.getCell(this._x, y).isEmpty())
                return false;
        }
        return true;
    }

    isEmptyHorizontal(target: Cell): boolean {
        if (this._y !== target._y)
            return false;

        const min = Math.min(this._x, target._x);
        const max = Math.max(this._x, target._x);

        for (let x = min + 1; x < max; x++){
            if (!this._board.getCell(x, this._y).isEmpty())
                return false;
        }

        return true;
    }

    isEmptyDiagonal(target: Cell): boolean {
        const absX = Math.abs(this._x - target._x);
        const absY = Math.abs(this._y - target._y);
        if (absX !== absY)
            return false;

        const dy = this._y < target._y ? 1 : -1;
        const dx = this._x < target._x ? 1 : -1;

        for (let i = 1; i < absX; i++){
            if (!this._board.getCell(this._x + dx * i, this._y + dy * i).isEmpty())
                return false;
        }

        return true;
    }

    public isCellUnderAttack(target: Cell, color: Colors | undefined): boolean {
        let targetUnderAttack: boolean = false;
        this._board._cells.forEach((element) => {
            element.forEach((cell) => {
                if (cell._figure?._color !== color) {
                    if (
                        cell._figure?._name === FigureNames.PAWN &&
                        this._figure?.canPawnAttack(target)
                    ) {
                        targetUnderAttack = true;
                    }

                    if (
                        cell._figure?.canMove(target) &&
                        cell._figure?._name !== FigureNames.PAWN
                    ) {
                        targetUnderAttack = true;
                    }
                }
            });
        });
        return targetUnderAttack;
    }

    setFigure(figure: Figure) {
        this._figure = figure;
        this._figure._cell = this;
    }

    public moveFigure(targetCell: Cell) {
        if (this._figure && this._figure?.canMove(targetCell)){
            this._figure.moveFigure(targetCell);
            targetCell.setFigure(this._figure)
            this._figure = null;
        }
    }
}
