import {Figure, FigureNames} from "./Figure";
import {Colors} from "../Colors";
import {Cell} from "../Cell";
import blackImg from "../../resourses/images/black-pawn.png";
import whiteImg from "../../resourses/images/white-pawn.png";

export class Pawn extends Figure {

    _isFirstStep: boolean = true;

    constructor(color: Colors, cell: Cell) {
        super(color, cell);
        this._img = color === Colors.BLACK ? blackImg : whiteImg;
        this._name = FigureNames.PAWN;
    }

    private isPathObstructed(target: Cell): boolean {
        const direction = this._cell._figure?._color === Colors.BLACK ? 1 : -1;
        const startY = this._cell._y + direction;
        const endY = target._y;
        const step = direction > 0 ? 1 : -1;

        for (let y = startY; y !== endY; y += step) {
            if (!this._cell._board.getCell(target._x, y).isEmpty()) {
                return true;
            }
        }

        return false;
    }

    canMove(target: Cell): boolean {
        if(!super.canMove(target))
            return false;

        const direction = this._cell._figure?._color === Colors.BLACK ? 1 : -1;
        const firstStepDirection = this._cell._figure?._color === Colors.BLACK ? 2 : -2;

        // TODO: пофиксить переход через фигуру на первом ходу
        if ((target._y === this._cell._y + direction || this._isFirstStep && (target._y === this._cell._y + firstStepDirection))
            && target._x === this._cell._x
            && this._cell._board.getCell(target._x, target._y).isEmpty()) {
            return true;
        }

        if (target._y === this._cell._y + direction
            && (target._x === this._cell._x + 1 || target._x === this._cell._x - 1)
            && this._cell.isEnemy(target)) {
            return true;
        }

        return false;
    }

    moveFigure(target: Cell) {
        super.moveFigure(target);
        this._isFirstStep = false;
    }
}
